function CertificateCard(props){
    return (
        <div className='cert-card'>
            <img src={props.certificateScreenshot} alt={props.certificateTitle} />
            <div className='courseProvider'>
                {props.courseProviders.map(e => <span key={e.name}>{e.logo}{e.name} </span>)}
            </div>
            <h2>{props.certificateTitle}</h2>
            <a href={props.verificationLink} className="cert-link" target="_blank" rel="noopener noreferrer">
                Verify Certificate
            </a>
            <p className="certificateType">{props.certificationType}</p>
      </div>
    )
}

